        public static void GetProperty(ushort type, ushort action, byte[] body, NetworkStream stream)
        {

            action++;
            GetPropertyReq request = GetPropertyReq.Deserialize(body);

            Console.WriteLine($"Get Property of UserID={request.UserID}");


            GetPropertyRes response = new GetPropertyRes();


            var itemInfo = ServerHolder.DatabaseInstance.Select<ModelInventory>(ModelInventory.GetInventory, new
            {
                OID = request.UserID,
            });

            response.Type = type;
            response.Action = action;

            List<Item> Items = new List<Item>();


            foreach (var item in itemInfo)
            {

                Console.WriteLine($"ServerID={item.ServerID}, ItemID={item.ItemID}");
                Items.Add(new Item
                {
                    ServerID = item.ServerID,
                    ItemID = item.ItemID,
                    ItemEnd = item.ItemEnd,
                    StatMod = 0,
                    ItemType = 0
                });

            }

            response.Items = Items;


            string hexOutput = string.Join(", ", response.Serialize().Select(b => $"0x{b:X2}"));

            Console.WriteLine($"Property Output: {hexOutput}");

            _ = ServerHolder.ServerInstance.SendPacket(response.Serialize(), stream);


