        public static void BuyItem(ushort type, ushort action, byte[] body, NetworkStream stream)
        {
            action++;

            var player = _playerManager.GetPlayerByStream(stream);


            BuyItemReq request = BuyItemReq.Deserialize(body);
            Console.WriteLine($"Buying ItemID={request.ItemID}, ItemEnd={request.ItemEnd}");

            BuyItemRes response = new BuyItemRes();

            var CashQueryCarats = ServerHolder.DatabaseInstance.SelectFirst<ModelAccount>(ModelAccount.QueryCashCarats, new
            {
                ID = player.UserID,

            });

            if (CashQueryCarats != null)
            {
                if (CashQueryCarats.Carats > 0)
                {
                    response.Type = type;
                    response.Action = action;

                    ServerHolder.DatabaseInstance.Execute(ModelInventory.InsertItem, new
                    {
                        OID = player.UserID,
                        ID = request.ItemID,
                        END = request.ItemEnd
                    });

                    var ServerID = ServerHolder.DatabaseInstance.SelectFirst<ModelInventory>(ModelInventory.GetServerID, new
                    {
                        OID = player.UserID,
                        ID = request.ItemID
                    });

                    response.ServerID = ServerID.ServerID;
                    response.Carats = 5000000; // Remaining Currency after purchase, automatically goes to carat or rc. this is just visual rn.

                    _ = ServerHolder.ServerInstance.SendPacket(response.Serialize(), stream);

                }
            }

        }

        public static void OpenBox(ushort type, ushort action, byte[] body, NetworkStream stream)
        {
            action++;

            OpenBoxReq request = OpenBoxReq.Deserialize(body);

            int itemid = 0;
            int itemend = 0;
            int quantity = 0;


            Console.WriteLine($"Opening Box with ServerID={request.ServerID}");
            OpenBoxRes response = new OpenBoxRes();
            response.Type = type;
            response.Action = action;

            var player = _playerManager.GetPlayerByStream(stream);
            var query = ServerHolder.DatabaseInstance.SelectFirst<ModelInventory>(ModelInventory.GetItemFromServerID, new
            {
                SID = request.ServerID
            });

            if (query == null)
            {
                Console.WriteLine($"No inventory item found with ServerID={request.ServerID} for UserID={player.UserID}");
                Util.GenericFail(type, action, stream);
                return;
            }

            var box = BoxRegistry.GetBox(query.ItemID);

            if (box == null)
            {
                Console.WriteLine($"Box with ItemID={query.ItemID} not found in registry.");
                itemid = query.ItemID;
                itemend = 1000;

            }
            else
            {
                BoxItem item = box.GetRandomItem();

                if (item == null)
                {
                    Console.WriteLine($"No items found in box with ItemID={query.ItemID}.");
                    itemid = query.ItemID;
                    itemend = 1000;
                    quantity = 1;
                }
                else if (item.ItemID == 0)
                {
                    Console.WriteLine($"Box item has invalid ItemID=0 in box with ItemID={query.ItemID}.");
                    itemid = query.ItemID;
                    itemend = 1000;
                    quantity = 1;
                }
                else
                {
                    itemid = item.ItemID;
                    itemend = item.ItemType;
                    quantity = item.Quantity;
                }

            }

           

            // Delete Box.
            ServerHolder.DatabaseInstance.Execute(ModelInventory.DeleteItem, new
            {
                SID = request.ServerID
            });

            // Add Item
            ServerHolder.DatabaseInstance.Execute(ModelInventory.InsertItem, new
            {
                OID = player.UserID,
                ID = itemid,
                END = itemend
            });

            // Get new ServerID
            var ServerID = ServerHolder.DatabaseInstance.Select<ModelInventory>(ModelInventory.GetServerID, new
            {
                ID = itemid,
                OID = player.UserID,

            });


            response.ServerID = ServerID.FirstOrDefault()?.ServerID ?? 0;
            response.ItemID = itemid;
            response.ItemEnd = itemend;

            // Experimenting with these values.
            if (itemend == 1130)
            {
                response.Unknown1 = 0x54; // Placeholder for any additional fields
                response.Quantity = (byte)quantity;
            }

            if (itemend == 1000)
            {
                response.Unknown1 = 0x4E; // Placeholder for any additional fields
                response.Quantity = (byte)quantity;
            }

            string hexOutput = string.Join(", ", response.Serialize().Select(b => $"0x{b:X2}"));
            Console.WriteLine($"OpenBox Response: {hexOutput}");

            _ = ServerHolder.ServerInstance.SendPacket(response.Serialize(), stream);
            
        }


        public static void Enchant(ushort type, ushort action, byte[] body, NetworkStream stream)
        {
            action++;
            EnchantReq request = EnchantReq.Deserialize(body);
            EnchantRes response = new EnchantRes();

            response.Action = action;
            response.Type = type;

            response.EnchantGemID = request.EnchantGemID;
            response.StatMod = 1;

            _ = ServerHolder.ServerInstance.SendPacket(response.Serialize(), stream);

        }

